function uncheckAllSwitchers(){for(let n of switchers)n.classList.remove("promo__slide-switcher-checked")}function hideAllSlides(){for(let n of slides)n.classList.add("visually-hidden")}function checkSwitchersBy(n){const t=[...switchers].filter(t=>t.getAttribute("data-slide-number")===n);t.forEach(n=>n.classList.add("promo__slide-switcher-checked"))}function getSlideNumberBy(n){return n.getAttribute("data-slide-number")}function showSlide(n){const t=document.querySelector(`.promo__slide[data-slide-number="${n}"]`);t.classList.remove("visually-hidden")}const switchers=document.querySelectorAll(".promo__slide-switcher"),slides=document.querySelectorAll(".promo__slide");for(let n of switchers)n.addEventListener("click",()=>{uncheckAllSwitchers();hideAllSlides();const t=getSlideNumberBy(n);checkSwitchersBy(t);showSlide(t)});