function removeAllLinksSelection(){for(let n of servicesLinks)n.classList.remove("services__item-active")}function hideAllServices(){for(let n of services)n.classList.remove("services__item-content-active")}function selectLink(n){n.classList.add("services__item-active")}function showService(n){const t=document.querySelector(`.services__item-content-${n}`);t&&t.classList.add("services__item-content-active")}const servicesLinks=document.querySelectorAll(".services__item a"),services=document.querySelectorAll(".services__item-content");for(let n of servicesLinks)n.addEventListener("click",function(t){t.preventDefault();removeAllLinksSelection();hideAllServices();selectLink(n);const i=n.getAttribute("data-service-name");showService(i)});